(15, 88, 'Summer Fitness Challenge: 100 Mile Club!! ', 'bes-attitude-of-excellence', '<section class="article-content">\r\n<aside class="summary cf">\r\n<section class="summary cf"><br />\r\n<p><img src="images/header_image/children.jpg" alt="" width="248" height="175" />AAH! Those Lazy Days of Summer! Summer is a fun time to rest and relax, but we hope you will play outside and be as active as you can! Your body doesn’t need a long break! We would like for you to keep a log of the number of minutes you spend each day exercising.</p>\r\n</section>\r\n</aside>\r\n</section>', '', 1, 11, '2016-07-18 18:02:31', 46, '', '2016-07-18 18:32:44', 46, 0, '0000-00-00 00:00:00', '2016-07-18 18:02:31', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 16, 2, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 1, '*', ''),
