(28, 106, 'Bartlett Elementary School', 'our-school', '<p><img src="images/School/Bartlett_TN_Welcome_to_Bartlett.JPG" alt="" width="612" height="459" /></p>\r\n<p> <strong style="font-family: Helvetica, Arial, sans-serif; font-size: 12.16px; line-height: 1.3em;">The Bartlett Elementary School will ensure the success of all students in an everchanging world. Bartlett Elementary School is a community which:</strong></p>\r\n<ul>\r\n<li>promotes high academic standards and expectations for all students</li>\r\n<li>creates and supports a safe, pleasant, learning environment</li>\r\n<li>fosters self-esteem, respect, tolerance, and understanding of others</li>\r\n<li>encourages a cooperative effort among parents, students, teachers, staff, administrators, and the public</li>\r\n<li>helps young people reach their potential and become positive, contributing members of our community.</li>\r\n</ul>\r\n<p> </p>', '', 1, 11, '2016-07-19 05:12:42', 46, '', '2016-07-19 18:31:53', 46, 0, '0000-00-00 00:00:00', '2016-07-19 05:12:42', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"0","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 15, 2, '', '', 1, 2, '{"robots":"","author"