(29, 107, 'Library', 'library', '<p><img src="images/header_image/feature-brand-book.jpg" alt="" width="1330" height="608" /></p>\r\n<p><span class="_Tgc">A <b>school library</b> (or a <b>school library</b> media center) is a <b>library</b> within a <b>school</b> where students, staff, and often, parents of a public or private <b>school</b> have access to a variety of resources.</span></p>\r\n<p> </p>', '', 1, 2, '2016-07-19 05:14:25', 46, '', '2016-07-19 05:34:39', 46, 0, '0000-00-00 00:00:00', '2016-07-19 05:14:25', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 4, 1, '', '', 1, 0, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', ''),
